export const initialState = {
  mainPosts: [
    {
      id: 1,
      User: {
        id: 1,
        nickname: "Hyoilzzz",
      },
      content: "첫번째 게시글",
      Images: [{}, {}, {}],
      Comments: [
        {
          User: {
            nickname: "nero",
          },
          content: "개정판이 나왔네요.",
        },
        {
          User: {
            nickname: "hero",
          },
          content: "얼른 사고 싶어요.",
        },
      ],
    },
  ],
  imagePaths: [],
  postAdded: false,
};

const ADD_POST = "ADD_POST";
export const addPost = {
  type: ADD_POST,
};

const dummypost = {
  id: 2,
  content: "더미데이터",
  User: {
    id: 1,
    nickname: "hyoil",
  },
  Image: [],
  Conmments: [],
};

const reducer = (state = initialState, action) => {
  switch (action.type) {
    default:
      return state;
  }
};

export default reducer;
