import { all, fork } from "@redux-saga/core/effects";

function logInAPI(data) {
  return axios.post("/api/login", data);
}

// action 매개변수로 로그인 정보가 들어옴
// action.type : "~~"
// action.data : data
// loginAPI(action.data) ==> call(logInAPI, action.data)
function* logIn(action) {
  try {
    // const result = yield call(logInAPI, action.data);
    console.log("saga login");
    yield delay(1000);
    yield put({
      type: "LOG_IN_SUCCESS",
      data: action.data, // 성공결과가 담겨있음
    });
  } catch (err) {
    yield put({
      type: "LOG_IN_FAILURE",
      data: err.response.data, // 실패결과가 담겨있음
    });
  }
}

function logOutAPI() {
  return axios.post("/api/logout");
}

function* logOut() {
  try {
    // const result = yield call(logOutAPI);
    yield delay(1000);
    yield put({
      type: "LOG_OUT_SUCCESS",
      data: result.data, // 성공결과가 담겨있음
    });
  } catch (err) {
    yield put({
      type: "LOG_OUT_FAILURE",
      data: err.response.data, // 실패결과가 담겨있음
    });
  }
}

function* watchLogin() {
  yield takeLatest("LOG_IN_REQUEST", logIn); // LOG_IN 액션이 실행되면 logIn함수가 실행 , reducer와 saga가 동시에 실행됨
}

function* watchLogOut() {
  yield takeLatest("LOG_OUT_REQUEST", logOut);
}

export default function* userSaga() {
  yield all([fork(watchLogin), fork(watchLogOut)]);
}
