import PropTypes from "prop-types";
import { useCallback, useState } from "react";

const PostImages = ({ images }) => {
  const [showImagesZoom, setShowImagesZoom] = useState(false);

  const onZoom = useCallback(() => {
    setShowImagesZoom(true);
  }, []);

  if (images.length === 1) {
    return (
      <>
        <img
          role="presenttaion"
          src={images[0].src}
          alt={images[0].src}
          onClick={onZoom}
        ></img>
      </>
    );
  }
  if (images.length === 2) {
    return (
      <>
        <img
          role="presenttaion"
          width="50%"
          src={images[0].src}
          alt={images[0].src}
          onClick={onZoom}
        ></img>
        <img
          role="presenttaion"
          width="50%"
          src={images[1].src}
          alt={images[1].src}
          onClick={onZoom}
        ></img>
      </>
    );
  }

  return (
    <>
      <div>
        <img
          role="presenttaion"
          width="50%"
          src={images[0].src}
          alt={images[0].src}
          onClick={onZoom}
        ></img>
        <div
          role="presentation"
          style={{
            display: "inline-block",
            width: "50%",
            textAlign: "center",
            verticalAlign: "middle",
          }}
          onClick={onZoom}
        ></div>
      </div>
    </>
  );
};

PostImages.proptypes = {
  images: PropTypes.arrayOf(PropTypes.object),
};

export default PostImages;
